
CREATE DATABASE BookMasterDatabase

GO

CREATE TABLE Author
(
	IDAuthor INT PRIMARY KEY IDENTITY,
	FullName NVARCHAR(200) NOT NULL,
	PicturePath NVARCHAR(MAX),
	Description NVARCHAR(2000),
	IsDeleted BIT DEFAULT 0
)

CREATE TABLE "Status"
(
	IDStatus INT PRIMARY KEY IDENTITY,
	StatusName NVARCHAR(100) NOT NULL
)

CREATE TABLE Book
(
	IDBook INT PRIMARY KEY IDENTITY,
	Title NVARCHAR(1000) NOT NULL,
	IDAuthor INT FOREIGN KEY REFERENCES Author(IDAuthor) NOT NULL,
	Price DECIMAL NOT NULL,
	LoanPrice DECIMAL NOT NULL,
	ISBN NVARCHAR(1000) NOT NULL,
	PicturePath NVARCHAR(MAX),
	IDStatus INT FOREIGN KEY REFERENCES [Status](IDStatus) NOT NULL,
	Quantity INT NOT NULL,
	"Description" NVARCHAR(MAX),
	Demand INT DEFAULT 0
)

CREATE TABLE [User]
(
	IDUser INT PRIMARY KEY IDENTITY,
	UserCode NVARCHAR(11) NOT NULL, 
	FirstName NVARCHAR(100) NOT NULL,
	LastName NVARCHAR(100) NOT NULL,
	CreatedAt DATETIME NOT NULL,
	DeletedAt DATETIME NULL,
	DateOfBirth DATE,
	Email NVARCHAR(500) UNIQUE NOT NULL,
	EmailConfirmed BIT, 
	PasswordHash NVARCHAR(MAX),
	PasswordConfirmed BIT DEFAULT 0,
	CityName NVARCHAR(100),
	ZipCode INT,
	"Address" NVARCHAR(100),
	IsAdmin BIT DEFAULT 0,
	OIB NVARCHAR(11),
	WorkPosition NVARCHAR(100)
)

CREATE TABLE Purchase
(
	IDPurchase INT PRIMARY KEY IDENTITY,
	IDBook INT FOREIGN KEY REFERENCES Book(IDBook) NOT NULL,
	IDUser INT FOREIGN KEY REFERENCES [User](IDUser) NOT NULL,
	PurchaseDate DATETIME NOT NULL
)

CREATE TABLE Loan
(
	IDLoan INT PRIMARY KEY IDENTITY,
	IDBook INT FOREIGN KEY REFERENCES Book(IDBook) NOT NULL,
	IDUser INT FOREIGN KEY REFERENCES [User](IDUser) NOT NULL,
	LoanBeginDate DATETIME NOT NULL,
	LoanEndDate DATETIME NOT NULL,
	TotalDelayAmount DECIMAL DEFAULT 0,
	DelayPrice DECIMAL NOT NULL,
	IsSettled BIT DEFAULT 0
)







INSERT INTO [Status]([StatusName])
VALUES ('New'),('Used')
GO

INSERT INTO Author([FullName], [PicturePath], [Description])
VALUES ('Shel Silverstein', 'https://upload.wikimedia.org/wikipedia/commons/3/32/Portrait_of_Shel_Silverstein_in_c._1964_by_Jerry_Yulsman.jpg',
'Shel Silverstein was born on September 25, 1930 in Chicago, Illinois, and began writing and drawing at a young age. Silverstein is best known as the author of iconic books of prose and poetry for young readers. His works include such modern classics as A Light in the Attic (HarperCollins, 1981), recipient of the School Library Journal Best Books Award in 1982; Where the Sidewalk Ends (Harper & Row, 1974), a 1974 Michigan Young Readers Award winner;  and The Giving Tree (Harper & Row, 1964). Runny Babbit (HarperCollins, 2005), a posthumous poetry collection of spoonerisms, was conceived and completed before his death.,')
GO

